import React, { useState } from 'react';
import { useHistory } from 'react-router';

import { newGame , joinGame , IGame} from './gameApi';
import {  } from './board/boardApi';

function CreateGame() {
    // Opciones que se pueden seleccionar
    const options = ['Jugar contra la maquina', 'Jugar contra un amigo'];

    // HOOKS
    const [radio, setRadio] = useState(options[1]);
    const history = useHistory();

    // Al accionar boton Crear partida
    async function handleSubmit(event: React.FormEvent) {
        event.preventDefault();   // consume el evento


        try {
            const idGame = await newGame({ vsMaquina: radio === options[0]});

            history.push(`game/${idGame}/board`);          // Cambiar a router game/id/board (component Board)

        } catch (error) {
            console.log(error);
        }
    }


    return (
        <form onSubmit={(event) => handleSubmit(event)}>
            <fieldset>

                {
                    options.map(option =>
                        <div className="radio" key={option.toString()}>
                            <label>
                                <input type="radio" value={option} checked={radio === option} onChange={(event) => {setRadio(event.target.value)}} />
                                {option}
                            </label>

                            <br />
                        </div>
                    )
                }

                <input type="submit" value="Crear Partida" />

            </fieldset>
        </form>
    );
}


function UnirGame() {

    // HOOKS
    const [codigo, setValueInput] = useState(Number);
    const history = useHistory();

    // Al accionar boton Crear partida
    async function handleSubmit(event: React.FormEvent) {
        event.preventDefault();   // consume el evento

        if(!codigo) return;  // Si el input esta vacio retorna nada       // DEBERIA VALIDAR QUE AL ESTAR VACIO MUESTRE UN MENSAJE "El Codigo no puede estar vacio"

        try {
            const game: IGame = await joinGame(codigo, {});         // Une el user2 a la partida.



            history.push(`game/${game.id}/board`);          // Cambiar a router game/id/board (component Board)
        } catch (error) {
            console.log(error);
        }
    }


    return (
        <form onSubmit={(event) => handleSubmit(event)}>
            <fieldset>

                <label htmlFor="unirPartida">Código Partida</label>
                <input type="number" id="unirPartida" placeholder="Ingrese Código Partida" onChange={(event) => setValueInput(parseInt(event.target.value))} />

                <input type="submit" value="Unir Partida" />

            </fieldset>
        </form>
    );
}


export { CreateGame , UnirGame};
