import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from 'react-router';
import './boardStyle.css';

import { IUser } from '../../user/userApi';
import { IGame, getGame } from '../gameApi';
import { IBoard, IUpDateBoard, newBoard , editBoard, getBoard } from './boardApi';



function Board() {
    // HOOKS
    const [board, setBoard] = useState({} as IBoard);
    const [game, setGame] = useState({} as IGame);
    let { idGame, idBoard } = useParams();
    const history = useHistory();



    async function handleSelecCasilla(casilla: number) {

        if(board.win) return;       // Si ya hay ganador, no hacer nada

        const body: IUpDateBoard = { casilla: casilla }
        setBoard(await editBoard(game.id, board.id, body));
    }

    async function handleRevencha() {
        const boardNew = await newBoard(game.id, {});
        idBoard = boardNew.id;
        history.push(`/game/${idGame}/board/${boardNew.id}`);
    }


    function marcar(i: number) {
        if (!board.casillas) return;

        if (board.casillas[i] === game.user1) return 'X';
        if (board.casillas[i] === game.user2) return 'O';

        return;
    }


    useEffect(() => {

        async function buscarJuego() {

            setGame(await getGame(idGame));
            const resBoard = await getBoard(idGame, idBoard);

            setBoard(resBoard);

            if(resBoard.win){               
                clearInterval(interval);
                return;
            }
        }

        let interval = setInterval(
            () => buscarJuego(),
            500
        );

        return (() => {
            clearInterval(interval);
        })
    }, []);


    return (
        <div className="grid-container">

            <Users board={board} game={game} />

            <table id="tablero">
                <tbody>
                    <tr>
                        <td className="f1 c1" onClick={() => handleSelecCasilla(0)} >{marcar(0)} </td>         {/*  { board.casillas && board.casillas[0] === game.user1 ? 'X' : 'O' }  */}
                        <td className="f1 c2" onClick={() => handleSelecCasilla(1)} >{marcar(1)}</td>
                        <td className="f1 c3" onClick={() => handleSelecCasilla(2)} >{marcar(2)}</td>
                    </tr>
                    <tr>
                        <td className="f2 c1" onClick={() => handleSelecCasilla(3)} >{marcar(3)}</td>
                        <td className="f2 c2" onClick={() => handleSelecCasilla(4)} >{marcar(4)}</td>
                        <td className="f2 c3" onClick={() => handleSelecCasilla(5)} >{marcar(5)}</td>
                    </tr>
                    <tr>
                        <td className="f3 c1" onClick={() => handleSelecCasilla(6)} >{marcar(6)}</td>
                        <td className="f3 c2" onClick={() => handleSelecCasilla(7)} >{marcar(7)}</td>
                        <td className="f3 c3" onClick={() => handleSelecCasilla(8)} >{marcar(8)}</td>
                    </tr>
                </tbody>
            </table>

            <button className="w3-button w3-black w3-round-xlarge w3-small revancha" onClick={() => handleRevencha()} >Revancha</button>
        </div>
    );
}


function Users(prop: any) {
    // HOOKS
    const [user1, setUser1] = useState({} as IUser);
    const [user2, setUser2] = useState({} as IUser);

    return (
        <div className="container-user">
            {/* User 1 */}
            <div className="w3-card-4 w3-col l3 w3-margin">
                <div className="partidasGanadas">
                    <label>Partidas ganadas: {prop.game.winUser1}</label>
                </div>
                <header className="w3-container w3-blue">
                    <h2>{user1.name}</h2>
                </header>

                <div className="w3-container">
                    <p> Tienes las 'X'</p>
                </div>

                <footer className="w3-container w3-blue">
                    <h5>{prop.board.turno === prop.game.user1 && 'Tu turno'}</h5>
                </footer>
            </div>

            {/* User 2 */}
            <div className="w3-card-4 w3-col l3 w3-margin w3-right">
                <div className="partidasGanadas">
                    <label>Partidas ganadas: {prop.game.winUser2}</label>
                </div>
                <header className="w3-container w3-blue">
                    <h2>{user2.name}</h2>
                </header>

                <div className="w3-container">
                    <p> Tienes los 'O'</p>
                </div>

                <footer className="w3-container w3-blue">
                    <h5>{prop.board.turno === prop.game.user2 && 'Tu turno'}</h5>
                </footer>
            </div>

        </div>
    );
}


export { Board };
