import React, { useState } from 'react';


function CrearGame() {
    const options = ['Jugar contra la maquina' , 'Jugar contra un amigo'];

    const [radio, setRadio] = useState(options[1]);

    function handleSubmit(event: React.FormEvent) {
        event.preventDefault();   // consume el evento

        if(!radio) return;  // Si el input esta vacio retorna nada // DEBERIA VALIDAR QUE AL ESTAR VACIO MUESTRE UN MENSAJE "Seleccione uno de las opciones"

        console.log(radio);
    }


    return (
        <form onSubmit={(event) => handleSubmit(event)}>
            <fieldset>
                <div className="radio">
                    <label>
                        <input type="radio" name="juego" value={options[0]} checked={radio === options[0]} onChange={(event) => setRadio(event.target.value)}/>
                        Jugar contra la maquina
                    </label>

                    <br />

                    <label>
                        <input type="radio" name="juego" value={options[1]} checked={radio === options[1]} onChange={(event) => setRadio(event.target.value)}/>
                        Jugar contra un amigo
                    </label>
                </div>

                <input type="submit" value="Crear Partida" />
            </fieldset>
        </form>
    );
}

export { CrearGame };
