            setGame(game);
            setBoard(await getBoard(idGame, resGame.ultBoard ? resGame.ultBoard : 0));

            setUser1(resGame.user1);
            setUser2(resGame.user2);
        }

        buscarUsers();
        */

        if(props.game != {} as IGame) console.log("hpña");//setUser1(props.game.user1);
        //if(props.game)setUser2(props.game.user2);

    },);

    return (
        <div className="container-user">
            {/* User 1 */}
            <div className="w3-card-4 w3-col l3 w3-margin">
                <div className="partidasGanadas">
                    <label>Partidas ganadas: {game.winUser1}</label>
                </div>
                <header className="w3-container w3-blue">
                    <h2>{user1.name}</h2>
                </header>

                <div className="w3-container">
                    <p> Tienes las 'X'</p>
                </div>

                <footer className="w3-container w3-blue">
                    <h5>{board.turno === user1.token && 'Tu turno'}</h5>
                </footer>
            </div>

            {/* User 2 */}
            <div className="w3-card-4 w3-col l3 w3-margin w3-right">
