                    <label>Partidas ganadas: {game.winUser2}</label>
                </div>
                <header className="w3-container w3-blue">
                    <h2>{user2.name}</h2>
                </header>

                <div className="w3-container">
                    <p> Tienes los 'O'</p>
                </div>

                <footer className="w3-container w3-blue">
                    <h5>{board.turno === user2.token && 'Tu turno'}</h5>
                </footer>
            </div>

        </div>
    );
}


export { Board, Users };
