import React, { useEffect } from 'react';
import { useHistory } from 'react-router';

import { IGame, getGame } from './gameApi';


function WaitJoining() {
    // HOOKS
    const history = useHistory();
    const { game } : any = history.location.state;


    async function isJoined() {
        const resGame :IGame = await getGame(game.id);

        if (resGame.user2) {
            history.push(`/game/${game.id}/board/` , {game: resGame});       // Te redirige al juego inicial
        }
    }


    useEffect(() => {
        console.log(game);

        let timerID = setInterval(
            () => isJoined(),
            3000
        );

        return (() => {
            clearInterval(timerID);
        })
    }, []);



    return (
        <div>

            <h2> Pasale a tu amigo el codigo: {game.id}</h2>

            <h3> Esperando ...</h3>

        </div>
    );
}


export { WaitJoining };
