import axios from 'axios';

// VARIABLES
const URLBase = 'http://127.0.0.1:3000/user/';


// INTERFACES
interface IUser {
    id: number,
    name: string,
}

interface IUpUser {
    name: string,
}


// GET USER
async function findUserByToken(): Promise<IUser>  {
    try {
        const res= (await axios.get(URLBase + 'whoami')).data;

        if(res.status === "ERROR") return Promise.reject(res.response.err);  // Si hubo un error responde el error

        return Promise.resolve(res.response.result as IUser);
    } catch (error) {
        return Promise.reject(error);
    }
}


// POST USER
async function postUser(body: IUpUser): Promise<string> {
    try {
        const res = (await axios.post (URLBase,body)).data;         // Crea el usuario en la API 

        if(res.status === "ERROR") return Promise.reject(res.response.err);  // Si hubo un error responde el error

        return Promise.resolve(res.response.result.token);    // Devuelve el token del user creado

    } catch (error) {
        return Promise.reject(error);
    } 
}

export { findUserByToken , postUser};
