import React, { useEffect } from 'react';
import { useParams } from 'react-router';

import { getGame } from './gameApi';


function WaitJoining() {
    let timerID: any;
    // HOOKS
    const { idGame } = useParams();


    async function isJoined() {
        const game = await getGame(idGame);

        if (game.user2) {
            clearInterval(timerID);

        }
    }


    useEffect(() => {
        timerID = setInterval(
            () => isJoined(),
            3000
        );

        return (() => {
            clearInterval(timerID);
        })
    }, []);



    return (
        <div>

            <h2> Pasale a tu amigo el codigo: {idGame}</h2>

            <h3> Esperando ...</h3>

        </div>
    );
}


export { WaitJoining };
