import React, { useState } from 'react';
import { useHistory } from 'react-router';

import { joinGame , IGame} from './gameApi';

const gameInit: IGame = {idGame: 0, user1: '', user2: '', winUser1: 0, winUser2: 0, vsMaquina: false};

function UnirGame() {

    // HOOKS
    const [codigo, setValueInput] = useState(Number);
    const [game, setGame] = useState(gameInit);
    const history = useHistory();

    // Al accionar boton Crear partida
    async function handleSubmit(event: React.FormEvent) {
        event.preventDefault();   // consume el evento

        if(!codigo) return;  // Si el input esta vacio retorna nada       // DEBERIA VALIDAR QUE AL ESTAR VACIO MUESTRE UN MENSAJE "El Codigo no puede estar vacio"

        try {
            const resGame: IGame = await joinGame(codigo, {});         // Une el user2 a la partida.
            setGame(resGame);

            history.push(`game/${game.idGame}/board`)
        } catch (error) {
            console.log(error);
        }
    }


    return (
        <form onSubmit={(event) => handleSubmit(event)}>
            <fieldset>

                <label htmlFor="unirPartida">Código Partida</label>
                <input type="number" id="unirPartida" placeholder="Ingrese Código Partida" onChange={(event) => setValueInput(parseInt(event.target.value))} />

                <input type="submit" value="Unir Partida" />

            </fieldset>
        </form>
    );
}

export { UnirGame };
