import React, { useState } from 'react';
import { useHistory, useParams } from 'react-router';
import './boardStyle.css';

import { IUser } from '../../user/userApi';
import { IGame } from '../gameApi';
import { IBoard } from './boardApi';



function Board() {
    // HOOKS
    const [board, setBoard] = useState ({} as IBoard);

    return (
        <div className="grid-container">

            <Users board={board} />

            <table id="tablero" >
                <tr>
                    <td className="f1 c1" id="0" >1</td>
                    <td className="f1 c2" id="1" >2</td>
                    <td className="f1 c3" id="2" >3</td>
                </tr>
                <tr>
                    <td className="f2 c1" id="3" >4</td>
                    <td className="f2 c2" id="4" >5</td>
                    <td className="f2 c3" id="5" >6</td>
                </tr>
                <tr>
                    <td className="f3 c1" id="6" >7</td>
                    <td className="f3 c2" id="7" >8</td>
                    <td className="f3 c3" id="8" >9</td>
                </tr>
            </table>

            <button className="w3-button w3-black w3-round-xlarge w3-small revancha">Revancha</button>
        </div>
    );
}


function Users(prop:any) {
    // HOOKS
    const [user1, setUser1] = useState({} as IUser);
    const [user2, setUser2] = useState({} as IUser);
    const [game, setgame] = useState({} as IGame);

    return (
        <div className="container-user">
            {/* User 1 */}
            <div className="w3-card-4 w3-col l3 w3-margin">
                <div className="partidasGanadas">
                    <label>Partidas ganadas: {game.winUser1}</label>
                </div>
                <header className="w3-container w3-blue">
                    <h2>{user1.name}</h2>
                </header>

                <div className="w3-container">
                    <p> Tienes las 'X'</p>
                </div>

                <footer className="w3-container w3-blue">
                    <h5>{ prop.board.turno === game.user1 && 'Tu turno'}</h5>
                </footer>
            </div>

            {/* User 2 */}
            <div className="w3-card-4 w3-col l3 w3-margin w3-right">
                <div className="partidasGanadas">
                    <label>Partidas ganadas: {game.winUser2}</label>
                </div>
                <header className="w3-container w3-blue">
                    <h2>{user2.name}</h2>
                </header>

                <div className="w3-container">
                    <p> Tienes los 'O'</p>
                </div>

                <footer className="w3-container w3-blue">
                <h5>{ prop.board.turno === game.user2 && 'Tu turno'}</h5>
                </footer>
            </div>

        </div>
    );
}


export { Board };
