import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router';

import { setToken , getToken , deleteToken  } from '../helpers/auth-helpers';

import { postUser, findUserByToken , IUser} from './userApi';


// Variables
const userInit: IUser = {id:0 , name:''};


function Login() {
    const [value, setValue] = useState('');
    const [user, setUser] = useState(userInit);

    const history = useHistory();

    async function handleSubmit(event: React.FormEvent) {
      event.preventDefault();   // consume el evento

      if(!value) return;  // Si el input esta vacio retorna nada
    // DEBERIA VALIDAR QUE AL ESTAR VACIO MUESTRE UN MENSAJE "EL NOMBRE NO PUEDE ESTAR VACIO"

      try{
        const token: string = await postUser({name: value});

        setToken(token);    // Setea el token al localStorage

        history.push('/game');
      }catch(error){
        console.log(error);
        // DEBERIA VALIDAR QUE AL EXISTIR USUARIO MUESTRE UN MENSAJE "EL NOMBRE YA EXISTE"
      }
    }

    useEffect( () => {
      if(getToken()) {
        findUserByToken()
        .then((result) => {
          console.log(result);
        })
        .catch((error) => {
          console.log(error);
        });

        history.push('/game');

      }  // Si hay un token guardado en el storage salta a game

    } , []);


    return (
      <form onSubmit={(event) => handleSubmit(event)}>
        <label>
          Name:
          <input type="text" value={value} placeholder="Ingrese Nombre" onChange={(event) => setValue(event.target.value)} />
        </label>
        <input type="submit" value="Ingresar"/>
      </form>
    );
  }

  export {Login};
