import React, { useEffect } from 'react';
import { useParams , useHistory} from 'react-router';

import { getGame } from './gameApi';


function WaitJoining() {
    let timerID: any;
    // HOOKS
    const { idGame } = useParams();
    const history = useHistory();


    async function isJoined() {
        const game = await getGame(idGame);

        if (game.user2) {
            clearInterval(timerID);
            history.push(`/game/${game.id}/board/`);       // Te redirige al juego inicial
        }
    }


    useEffect(() => {
        console.log(history);

        timerID = setInterval(
            () => isJoined(),
            3000
        );

        return (() => {
            clearInterval(timerID);
        })
    }, []);



    return (
        <div>

            <h2> Pasale a tu amigo el codigo: {idGame}</h2>

            <h3> Esperando ...</h3>

        </div>
    );
}


export { WaitJoining };
