import axios from 'axios';
import { findUserByToken } from '../user/userApi';

// VARIABLES
const URLBase = 'http://127.0.0.1:3000/game/';


// INTERFACES
export interface IGame {
    idGame: number,
    user1: string,
    user2: string,
    winUser1: number,
    winUser2: number,
    vsMaquina: boolean,
}

interface IUpGame {
    vsMaquina: boolean,
}


// POST GAME
async function postGame(body: IUpGame): Promise<number> {
    try {
        const res = (await axios.post (URLBase,body)).data;         // Crea el game en la API 

        if(res.status === "ERROR") return Promise.reject(res.response.err);  // Si hubo un error responde el error

        return Promise.resolve(res.response.result.id as number);    // Devuelve el id del game creado

    } catch (error) {
        return Promise.reject(error);
    } 
}

export { postGame };
