import React, { useState } from 'react';
import { useHistory } from 'react-router';

import { newGame } from './gameApi';

function CrearGame() {
    // Opciones que se pueden seleccionar
    const options = ['Jugar contra la maquina', 'Jugar contra un amigo'];

    // HOOKS
    const [radio, setRadio] = useState(options[1]);
    const history = useHistory();

    // Al accionar boton Crear partida
    async function handleSubmit(event: React.FormEvent) {
        event.preventDefault();   // consume el evento


        try {
            const idGame = await newGame({ vsMaquina: radio === options[0]});

            history.push(`game/${idGame}/board`);          // Cambiar a router game/id/board (component Board)

        } catch (error) {
            console.log(error);
        }
    }


    return (
        <form onSubmit={(event) => handleSubmit(event)}>
            <fieldset>

                {
                    options.map(option =>
                        <div className="radio" key={option.toString()}>
                            <label>
                                <input type="radio" value={option} checked={radio === option} onChange={(event) => {setRadio(event.target.value)}} />
                                {option}
                            </label>

                            <br />
                        </div>
                    )
                }

                <input type="submit" value="Crear Partida" />

            </fieldset>
        </form>
    );
}

export { CrearGame };
